import React from "react";
import { useState } from 'react';
import { Card, Button } from 'react-bootstrap';

export default function Compare(props) {
  const { info_data } = props;
  const [info1, setinfo1] = useState(""); // State for info 1
  const [info2, setinfo2] = useState(""); // State for info 2

  const filteredinfos1 = info_data.filter(info => {
    return info.name.toLowerCase().includes(info1.toLowerCase());
  });

  const filteredinfos2 = info_data.filter(info => {
    return info.name.toLowerCase().includes(info2.toLowerCase());
  });

  const displayCards = filteredinfos1.slice(0, 1).concat(filteredinfos2.slice(0, 1));

  return (
    <div>
      <div className="container">
        <h1 className="main-heading">Compare infos</h1>
        <p className="description">Can't decide between two infos? Enter their names down below to compare them!</p>
        <hr />
        <div className="row compare-label">
          <div className="col">
            <label htmlFor="info1" className="bold-label">Enter info 1:</label>
            <input
              type="text"
              id="info1"
              name="info1"
              className="compare-input-field"
              value={info1}
              onChange={(e) => setinfo1(e.target.value)}
            />
          </div>
          <div className="col">
            <label htmlFor="info2" className="bold-label">Enter info 2:</label>
            <input
              type="text"
              id="info2"
              name="info2"
              className="input-field"
              value={info2}
              onChange={(e) => setinfo2(e.target.value)}
            />
          </div>
        </div>
        <div className="compare-container">
            <Button variant="warning" className="compare-button">Compare</Button>
            <div className="card-container">
                {displayCards.map(info => (
                  <HomeCard key={info.id} info={info} />
                ))}
            </div>
        </div>
      </div>
    </div>
  );
}

function HomeCard({ info }) {
  return (
    <Card className="CompareCard">
      <Card.Img
        src={info.Picture_link}
        alt={info.name}
        style={{
          height: '260px',
          width: '100%',
          objectFit: 'cover',
          border: '3px solid #ddd'
        }}
      />
      <Card.Body>
        <Card.Title>{info.name}</Card.Title>
        <Card.Text>
            Rating: 
            <span className="compare-rating" style={{ color: 'rgb(255, 183, 0)'}}> {info.star} &#9733;</span>
        </Card.Text>
        <Card.Text>Price: {info.price}</Card.Text>
        <Card.Text>Location: {info.area}</Card.Text> 
        <Card.Text>Food Type: {info.category}</Card.Text>
      </Card.Body>
    </Card>
  );
}
