import React, { useState, useEffect } from "react";
import { AllCards } from "./pages/QuizResult.js";
import { FilterForm } from './pages/QuizForm.js';
import { Routes, Route, Navigate } from 'react-router-dom';
import _ from 'lodash';
import Header from "./components/header";
import Footer from "./components/footer";
import LoginPage from "./pages/SignIn";
import { getAuth } from "firebase/auth"
import { useAuthState } from "react-firebase-hooks/auth";
import LogOutModal from "./pages/LogOut.js";
import CommunityPage from './pages/CommunityPage';
import CommunityDetail from './pages/CommunityDetail';
import HomePage from './pages/HomePage';
import ComparePage from './pages/ComparePage';

const App = (props) => {
  // quiz checkbox
  const [RestaurantData, setRestaurantData] = useState(props.restaurants_data);
  const [order, setOrder] = useState(props.restaurants_data);
  const [filterOptions, setFilterOptions] = useState(() => {
    if (props.card && props.card.category) {
      const combinedBadge = [...props.restaurants_data.category.split(', '), props.restaurants_data.star, props.restaurants_data.price];
      return combinedBadge;
    } else {
      return [];
    }
  });

  const applySort = (value) => {
    if (value === '') {
      setOrder(_.sortBy(order, ['id']))
    }
  }

  const applyFilters = (e) => {
    const filteredData = RestaurantData.filter((restaurant) => {
      return (
        passCategoryFilter(restaurant) ||
        passStarRatingFilter(restaurant) &&
        passPriceFilter(restaurant)
      );
    });

    setOrder(filteredData);
  };

  const passCategoryFilter = (restaurant) => {
    const selectedCategories = Object.entries(filterOptions)
      .filter(([key, value]) => value && key !== 'Seafood')
      .map(([key]) => key);

    const categoriesByList = {
      list1: ['American', 'Chinese', 'Indian', 'Hawaiian', 'Vietnamese', 'Japanese', 'Mexican', 'Mediterranean', 'Italian', 'Korean'],
      list2: ['Bar', 'Barbeque', 'Venues & Event Spaces', 'Cafe', 'Breakfast & Brunch Bakeries'],
      list2: ['Bar', 'Barbeque', 'Venues & Event Spaces', 'Cafe', 'Breakfast & Brunch', 'Bakeries'],
      list2: ['Bar', 'Barbeque', 'Venues & Event Spaces', 'Cafe', 'Breakfast & Brunch', 'Bakeries'],
    };

    const selectedCategoriesByList = {};

    // Group selected categories by list
    selectedCategories.forEach(category => {
      const list = Object.keys(categoriesByList).find(listKey => categoriesByList[listKey].includes(category));
      if (list) {
        if (!selectedCategoriesByList[list]) {
          selectedCategoriesByList[list] = [];
        }
        selectedCategoriesByList[list].push(category);
      }
    });

    const listFilters = Object.values(selectedCategoriesByList).map(selectedCategoriesList => {
      return restaurant.category.some(category => selectedCategoriesList.includes(category));
    });

    const isSameListFilter = listFilters.length > 1 ? listFilters.reduce((a, b) => a || b) : false;
    const isDifferentListFilter = listFilters.length === 1 ? listFilters[0] : false;

    return (
      !restaurant.category.includes('Seafood') ||
      isSameListFilter ||
      isDifferentListFilter
    );
  };

  const passStarRatingFilter = (restaurant) => {
    const selectedStarRatings = Object.entries(filterOptions)
      .filter(([key, value]) => value && key.match(/^\d+$/))
      .map(([key]) => key);

    return selectedStarRatings.includes(restaurant.star);
  };

  const passPriceFilter = (restaurant) => {
    const selectedPrices = Object.entries(filterOptions)
      .filter(([key, value]) => value && key.match(/^\$/))
      .map(([key]) => key);

    return selectedPrices.includes(restaurant.price);
  };


  const handleCheckbox = (e) => {
    let name = e.target.name;
    setFilterOptions({ ...filterOptions, [name]: !filterOptions[name] })
  }

  // Start up user authentication
  const [user, loading] = useAuthState(getAuth());
  const currentUser = user;

  // Dealing with log out modal
  const [showLogOut, setShowLogOut] = useState(false);

  return (

    <div className="background">
      <Header user={currentUser} loading={loading} showLogOut={setShowLogOut} />
      <LogOutModal show={showLogOut} setShowLogOut={setShowLogOut} />
      <Routes>
        <Route path="homepage" element={<HomePage restaurants_data={props.restaurants_data} />} ></Route>
        <Route path="community" element={<CommunityPage restaurants_data={props.restaurants_data} />} ></Route>
        <Route path="community/:restaurantName" element={<CommunityDetail restaurants_data={props.restaurants_data} comment_data={props.comment_data}/>} ></Route>
        <Route path="quiz" element={<FilterForm applySortCallback={applySort} applyFilterCallback={applyFilters} data={RestaurantData} filterOptions={filterOptions} handleCheckbox={handleCheckbox} />} >
          <Route index element={<AllCards data={order} />} />
        </Route>
        <Route path="compare" element={<ComparePage restaurants_data={props.restaurants_data} />} ></Route>
        <Route path="signin" element={<LoginPage user={currentUser} loading={loading} />} />
        <Route path="*" element={<Navigate to="/homepage" />} />
      </Routes>
      <Footer />
    </div>

  );
}


export default App;
