import React from "react";
import { useState } from 'react';
import { Card, Button } from 'react-bootstrap';

export default function Compare(props) {
  const { data } = props;
  const [data1, setdata1] = useState(""); // State for data 1
  const [data2, setdata2] = useState(""); // State for data 2

  const filtereddatas1 = data.filter(data => {
    return data.name.toLowerCase().includes(data1.toLowerCase());
  });

  const filtereddatas2 = data.filter(data => {
    return data.name.toLowerCase().includes(data2.toLowerCase());
  });

  const displayCards = filtereddatas1.slice(0, 1).concat(filtereddatas2.slice(0, 1));

  return (
    <div>
      <div className="container">
        <h1 className="main-heading">Compare datas</h1>
        <p className="description">Can't decide between two datas? Enter their names down below to compare them!</p>
        <hr />
        <div className="row compare-label">
          <div className="col">
            <label htmlFor="data1" className="bold-label">Enter data 1:</label>
            <input
              type="text"
              id="data1"
              name="data1"
              className="compare-input-field"
              value={data1}
              onChange={(e) => setdata1(e.target.value)}
            />
          </div>
          <div className="col">
            <label htmlFor="data2" className="bold-label">Enter data 2:</label>
            <input
              type="text"
              id="data2"
              name="data2"
              className="input-field"
              value={data2}
              onChange={(e) => setdata2(e.target.value)}
            />
          </div>
        </div>
        <div className="compare-container">
            <Button variant="warning" className="compare-button">Compare</Button>
            <div className="card-container">
                {displayCards.map(data => (
                  <HomeCard key={data.id} data={data} />
                ))}
            </div>
        </div>
      </div>
    </div>
  );
}

function HomeCard({ data }) {
  return (
    <Card className="CompareCard">
      <Card.Img
        src={data.Picture_link}
        alt={data.name}
        style={{
          height: '260px',
          width: '100%',
          objectFit: 'cover',
          border: '3px solid #ddd'
        }}
      />
      <Card.Body>
        <Card.Title>{data.name}</Card.Title>
        <Card.Text>
            Rating: 
            <span className="compare-rating" style={{ color: 'rgb(255, 183, 0)'}}> {data.star} &#9733;</span>
        </Card.Text>
        <Card.Text>Price: {data.price}</Card.Text>
        <Card.Text>Location: {data.area}</Card.Text> 
        <Card.Text>Food Type: {data.category}</Card.Text>
      </Card.Body>
    </Card>
  );
}
