import React, { useState } from "react";
import { Card } from 'react-bootstrap';

export default function HomePage(props) {
  const data = props.data
  const [searchQuery, setSearchQuery] = useState(""); // State for search query
  // Filter datas based on search query
  const filtereddatas = data.filter(data => {
    return data.name.toLowerCase().includes(searchQuery.toLowerCase());
  });

  return (
    <div>
      <div className="sentence-container">
        <Sentence />
        <div className="search-bar-container">
            <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
        </div>
      </div>
      <HomeBody data={filtereddatas} />
    </div>
  );
}

function Sentence() {
  return (
    <main>
      <div className="home">
        <p className="welcome">Welcome to iDawg<span className="highlight">Know</span></p>
        <p className="what">What do you care today?</p>
      </div>
    </main>
  );
}

function SearchBar({ searchQuery, setSearchQuery }) {
    const handleInputChange = (event) => {
      setSearchQuery(event.target.value);
    };

    return (
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search For Information"
          value={searchQuery}
          onChange={handleInputChange}
          className="search-input"
        />
        <button className="search-button">
          <i className="fas fa-search"></i>
        </button>
      </div>
    );
  }

function HomeBody({ data }) {
  return (
    <div className="community">
      <div className="container">
        {data.map((data) => {
          return <HomeCard key={data.id} data={data} />;
        })}
      </div>
    </div>
  );
}

function HomeCard({ data }) {
  return (
    <Card className="Card">
      <Card.Img
        src={data.Picture_link}
        alt={data.name}
        style={{
          height: '260px',
          width: '100%',
          objectFit: 'cover',
          border: '3px solid #ddd'
        }}
      />
      <Card.Body>
        <Card.Title>{data.name}</Card.Title>
        <Card.Text>Info Type: {data.category}</Card.Text>
      </Card.Body>
      <Card.Footer
        className="text-muted"
        style={{ backgroundColor: 'white', border: 'none' }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        </div>
      </Card.Footer>
    </Card>
  );
}
